mapper.select:
select
  id, title, content, view_count, like_count, comment_count, status, user_id, create_time, update_time, summary
from table_article


        and id = #{id}


        and title = #{title}


        and content = #{content}


        and view_count = #{viewCount}


        and like_count = #{likeCount}


        and comment_count = #{commentCount}


        and status = #{status}


        and user_id = #{userId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and summary = #{summary}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from table_article


        and id = #{id}


        and title = #{title}


        and content = #{content}


        and view_count = #{viewCount}


        and like_count = #{likeCount}


        and comment_count = #{commentCount}


        and status = #{status}


        and user_id = #{userId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and summary = #{summary}

select * from table_article where status = 1
order by view_count
desc limit 0, #{count};
