      id = #{id}
and   status != -1;

select
  id, title, content, view_count, like_count, comment_count, status, user_id, create_time, update_time, summary, thumbnail
from table_article


        and id = #{id}


        and title = #{title}


        and content = #{content}


        and view_count = #{viewCount}


        and like_count = #{likeCount}


        and comment_count = #{commentCount}


        and status = #{status}


        and user_id = #{userId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and summary = #{summary}


        and thumbnail = #{thumbnail}

select count(1)
from table_article


        and id = #{id}


        and title = #{title}


        and content = #{content}


        and view_count = #{viewCount}


        and like_count = #{likeCount}


        and comment_count = #{commentCount}


        and status = #{status}


        and user_id = #{userId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and summary = #{summary}


        and thumbnail = #{thumbnail}

select * from table_article where status = 0
order by view_count
desc limit 0, #{count}

