mapper.select:
select
  id, menu_name, path, component, visible, status, perms, icon, create_by, create_time, update_by, update_time, remark, parent
from table_menu


        and id = #{id}


        and menu_name = #{menuName}


        and path = #{path}


        and component = #{component}


        and visible = #{visible}


        and status = #{status}


        and perms = #{perms}


        and icon = #{icon}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and remark = #{remark}


        and parent = #{parent}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from table_menu


        and id = #{id}


        and menu_name = #{menuName}


        and path = #{path}


        and component = #{component}


        and visible = #{visible}


        and status = #{status}


        and perms = #{perms}


        and icon = #{icon}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and remark = #{remark}


        and parent = #{parent}

SELECT * FROM table_menu
WHERE `status` = #{status}
AND visible = #{visible}
AND parent = #{parent}
